import axios from 'axios';
import React, { useState } from 'react';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import ProgressBar from 'react-bootstrap/ProgressBar';

function Companies() {
  const [formData, setFormData] = useState({
    name: '',
    image: '',
  });
  const [file, setFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleImageUpload = () => {
    if (!file) {
      alert('Please select an image file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    axios.post('https://api.be-practical.com/aws/upload', formData, {
      onUploadProgress: (progressEvent) => {
        const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
        setUploadProgress(progress);
      },
    })
    .then((response) => {
      const imageUrl = response.data.url; // Adjust based on your API response structure
      setFormData((prevData) => ({ ...prevData, image: imageUrl }));
      setUploadProgress(0); // Reset progress bar
      alert('Image uploaded successfully.');
    })
    .catch((error) => {
      alert('Error uploading image: ' + error.message);
    });
  };

  const handleEventUpload = () => {
    axios.post('https://api.be-practical.com/api/companies', formData)
    .then((response) => {
      console.log(response.data);
      alert('Company created successfully.');
      setFormData({
        name: '',
        image: '',
      });
      setFile(null); // Clear selected file
    })
    .catch((error) => {
      alert('Error creating company: ' + error.message);
    });
  };

  return (
    <div className='bg-light'>
      <Tabs
        defaultActiveKey="home"
        id="uncontrolled-tab-example"
        className="mb-3"
      >
        <Tab eventKey="home" title="Create">
          <div className="container px-lg-5">
            <div className="card p-3">
              <h2 className='w-100 text-center'>Create Training Card</h2>
              <form>
                <div>
                  <label className='form-label'>Company Name: </label>
                  <input
                    type="text"
                    name="name"
                    onChange={handleInputChange}
                    value={formData.name}
                    className='form-control mb-3'
                    placeholder='Enter company name'
                  />
                </div>
                <div>
                  <label className='form-label'>Company Image: </label>
                  <input
                    type="file"
                    name="image"
                    onChange={handleFileChange}
                    className='form-control mb-3'
                    placeholder='Select an image file'
                  />
                </div>
                {uploadProgress > 0 && (
                  <ProgressBar now={uploadProgress} label={`${uploadProgress}%`} className='mb-3' />
                )}
                <button
                  type="button"
                  onClick={handleImageUpload}
                  className='btn btn-primary text-white mb-3'
                >
                  Upload Image
                </button>
                <button
                  type="button"
                  onClick={handleEventUpload}
                  className='btn btn-danger text-white'
                >
                  Create Training Card
                </button>
              </form>
            </div>
          </div>
        </Tab>
        <Tab eventKey="profile" title="Update">
          Tab content for Profile
        </Tab>
        <Tab eventKey="contact" title="All">
          Tab content for Contact
        </Tab>
      </Tabs>
    </div>
  );
}

export default Companies;
