// YourComponent.js
import React, { useEffect, useRef, useState } from 'react';
import { uploadDataToApi } from '../../Data/apiService';
import ReactQuill from 'react-quill';

const Create = () => {
    const quillRef = useRef();

    useEffect(() => {
      if (quillRef.current) {
        quillRef.current.getEditor().enable(true);
      }
    }, []);
  
    const settings = {
      toolbar: {
        container: [
          ['bold', 'italic', 'underline', 'strike'],
          [{ 'align': [] }],
          [{ 'list': 'ordered'}, { 'list': 'bullet' }],
          ['link', 'image'],
          ['table'],
        ],
      },
    };
  const [modules, setModules] = useState([]);
  const [newModule, setNewModule] = useState({ title: '', topics: '' });

  const handleModuleChange = (content) => {
    setNewModule((prevModule) => ({
      ...prevModule,
      topics: content,
    }));
  };

  const handleAddModule = () => {
    setModules((prevModules) => [...prevModules, newModule]);
    setNewModule({ title: '', topics: '' });
  };

  const handleUploadToApi = async () => {
    try {
      // Send the modules to the API
      const response = await uploadDataToApi({ modules });

      // Handle the API response as needed
      console.log('API Response:', response);
    } catch (error) {
      // Handle errors during API upload
      console.error('Error uploading modules to API:', error.message);
    }
  };
  const k = ()=>{
    console.log(modules)
  }

  // Your component UI and forms go here
  return (
    <div>
      {/* Module Form */}
      <h2>Add Module</h2>
      <label>
        Title:
        <input type="text" name="title" value={newModule.title} onChange={handleModuleChange} />
      </label>
      <br />
      <label>
        Topics:
        <ReactQuill name="topics"  ref={quillRef}   modules={settings} value={newModule.topics} onChange={handleModuleChange} className='editor' />
      </label>
      <br />
      <button onClick={handleAddModule}>Add Module</button>

      {/* Display the added modules */}
      <ul>
        {modules.map((mod, index) => (
          <li key={index}>
            <strong>{mod.title}</strong>: {mod.topics}
          </li>
        ))}
      </ul>

      {/* Upload Button */}
      <button onClick={k}>Upload Modules to API</button>
    </div>
  );
};

export default Create;
