import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

function AllSubCourses() {
    const {id} = useParams();
    const [allCourses, setAllCourses] = useState()
    const [loading, setLoading] = useState(true)
    useEffect(() => {
        const fetchAllCourses = async () => {
          try {
            const response = await axios.get(`http://localhost:3300/api/allcourses/${id}`);
            setLoading(false)
            console.log(response);
            setAllCourses(response.data)
          } catch (error) {
            setLoading(false)
            console.error('Error fetching courses:', error);
          }
        };
    
        fetchAllCourses();
      }, []);


  return (
    <div className='bg-white mt-3'>
     <div className='shadow p-2 mb-4 bg-success text-white'>
     <h1 className='fs-3'>{allCourses.courseName}</h1>
     </div>
     <span className="fs-5 p-3 d-block">{allCourses.subCourses.length} Courses</span>
       <ul className='all-courses p-0'>
       {
            allCourses?.subCourses?.map((item, index)=>(
                <li className='course-item rounded-3 d-flex justify-content-between align-items-center p-2 py-4 shadow-sm border mb-3' key={index}>
                    {/* <span className='fs-5'>{index+1} {item.courseName}</span> */}
                    <div className="icons d-flex gap-2 fs-5">
                    <a href=''><i class="bi bi-eye text-primary"></i></a>
                    <i class="bi bi-trash3 text-danger"></i>
                    <a href={`update/course/${item._id}`}><i class="bi bi-pencil-square text-success"></i></a>
                    
                    </div>
                </li>
            ))
        }
       </ul>
    </div>
  )
}

export default AllSubCourses
