import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // Import the styles

function BlogPost() {
  const [BlogsData, setBlogsData] = useState({
    title: '',
    subtitle:'',
    content: '',
    image:'',
  });

  const handleChange = (html) => {
    setEditorHtml(html);
  };
  const handleEventUpload = () => {
    axios.post('http://localhost:3300/api/blogs/add', BlogsData)
    .then((response) => {
        console.log(response.data)
        alert("success")
        setBlogsData({
            title: '',
            subtitle:'',
            content: '',
            image:'',
        })
    })
    .catch((error) => {
        alert(error);
    });
};

return (
<div className='p-3'>
    <form action="post" onSubmit={handleEventUpload}>
        <div className="row">
            <div className="col-12 col-md-4 col-lg-4">
                <div className="form-group">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input type="text" name='title' className="form-control" placeholder='Blog Title' />
                </div>
            </div>
            <div className="col-12 col-md-4 col-lg-4">
                <div className="form-group">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input type="text" name='subtitle' className="form-control" placeholder='Blog Subtitle' value={BlogsData.content} onChange={handleChange}  />
                </div>
            </div>
            <div className="col-12 col-md-4 col-lg-4">
                <div className="form-group">
                    <label htmlFor="url" className="form-label">Blogs Image URL</label>
                    <input type="text" name='title' className="form-control" placeholder='https://Blogs-image.jpg' value={BlogsData.image} onChange={handleChange}  />
                </div>
            </div>
        </div>

        <ReactQuill value={BlogsData.content} onChange={handleChange} />
    <button type='submit' className="btn btn-primary mt-3" onClick={post}>add Post</button>

    </form>

</div>
);
}

export default BlogPost;
