import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // Import the styles

function BlogPost() {
  const [BlogsData, setBlogsData] = useState('');

  const handleChange = (html) => {
    setEditorHtml(html);
  };
  const handleEventUpload = () => {
    axios.post('http://localhost:3300/api/blogs/add', BlogsData)
    .then((response) => {
        console.log(response.data)
        alert("success")
        setBlogsData({
            title: '',
            subtitle:'',
            content: '',
            image:'',
        })
    })
    .catch((error) => {
        alert(error);
    });
};


  return (
    <div className='p-3'>
        <form action="post" onSubmit={handleEventUpload}>
            <div className="form-group">
                <label htmlFor="title" className="form-label">Title</label>
                <input type="text" name className="form-control" />
            </div>
        </form>
      <ReactQuill value={editorHtml} onChange={handleChange} />
      <button className="btn btn-primary mt-3" onClick={post}>add Post</button>
      <div dangerouslySetInnerHTML={{ __html: editorHtml }} />
    </div>
  );
}

export default BlogPost;
