import axios from 'axios';
import React, { useEffect, useState } from 'react'

function All() {
    const [allCourses, setAllCourses] = useState([])
    const [loading, setLoading] = useState(true)
    useEffect(() => {
        const fetchAllCourses = async () => {
          try {
            const response = await axios.get('http://localhost:3300/api/allcourses');
            setLoading(false)
            console.log(response.data.courses);
            setAllCourses(response.data.courses)
          } catch (error) {
            setLoading(false)
            console.error('Error fetching courses:', error);
          }
        };
    
        fetchAllCourses();
      }, []);


  return (
    <div>
       <ul className='all-courses'>
       {
            allCourses.map((item, index)=>(
                <li className='course-item d-flex justify-content-between align-items-center p-2 shadow mb-3' key={index}>
                    <sapn className='fs-4'>{index+1} {item.courseName}</sapn>
                    <div className="icons d-flex gap-2 fs-4">
                    <i class="bi bi-trash3 text-danger"></i>
                    <i class="bi bi-pencil-square text-success"></i>
                    </div>
                </li>
            ))
        }
       </ul>
    </div>
  )
}

export default All
