// UploadData.js
import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { createCourse } from '../../Data/apiService';

const UploadData = () => {
  const [courseData, setCourseData] = useState({
    courseName: '',
    courseDuration: '',
    enrolledStudents: 0,
    modeOfTraining: '',
    courseVideo: '',
    minSalary: '',
    HighestSalary: '',
    BatchStarting: '',
    heroTitle: '',
    heroSubtitle: '',
    faqs: [],
    modules: [],
    programmingLanguages: [],
    instructors:[],
    courseImage:'',
    courseDescription:'',
    certification:'',
    courseFor:'',
    designation:'',
    salaryDescription:'',
    courseHeroPoints:''
  });

  const handleCertificationChange = (certification) => {
    setCourseData({ ...courseData, certification });
  };
  const handleCourseDescriptionChange = (courseDescription) => {
    setCourseData({ ...courseData, courseDescription });
  };
  const handleDesignationChange = (designation) => {
    setCourseData({ ...courseData, designation });
  };
  const handleSalaryDescriptionChange = (salaryDescription) => {
    setCourseData({ ...courseData, salaryDescription });
  };
  const handleCourseHeroPointsChange = (courseHeroPoints) => {
    setCourseData({ ...courseData, courseHeroPoints });
  };
  
  const handleCourseForChange = (courseFor) => {
    setCourseData({ ...courseData, courseFor });
  };

  const handleAddFAQ = () => {
    setCourseData((prevData) => ({
      ...prevData,
      faqs: [...prevData.faqs, { question: '', answer: '' }],
    }));
  };
  const handleAddInstructor = () => {
    setCourseData((prevData) => ({
      ...prevData,
      instructors: [...prevData.faqs, { name: '', email: '', enrolled:null }],
    }));
  };

  const handleAddModule = () => {
    setCourseData((prevData) => ({
      ...prevData,
      modules: [...prevData.modules, { title: '', topics: '' }],
    }));
  };

  const handleAddProgrammingLanguage = () => {
    setCourseData((prevData) => ({
      ...prevData,
      programmingLanguages: [
        ...prevData.programmingLanguages,
        { name: '', image: '' },
      ],
    }));
  };

  const handleUpload = async () => {
    try {
      const response = await createCourse(courseData);
      console.log('Course uploaded successfully:', response);
    } catch (error) {
      console.error('Error uploading course:', error.message);
    }
    console.log(courseData)
  };

  return (
    <div className='course-upload container-fluid p-3'>
      <h2>Course Details</h2>
      <div className="row">
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
        Course Name:
        </label>
        <input
          type="text"
          className='form-control'
          value={courseData.courseName}
          onChange={(e) =>
            setCourseData({ ...courseData, courseName: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      courseDuration:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.courseDuration}
          onChange={(e) =>
            setCourseData({ ...courseData, courseDuration: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      courseDuration:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.enrolledStudents}
          onChange={(e) =>
            setCourseData({ ...courseData, enrolledStudents: e.target.value })
          }
        />
       
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      modeOfTraining:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.modeOfTraining}
          onChange={(e) =>
            setCourseData({ ...courseData, modeOfTraining: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      courseVideo:
      </label> 
        <input
          type="text"
          className='form-control'
          value={courseData.courseVideo}
          onChange={(e) =>
            setCourseData({ ...courseData, courseVideo: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      minSalary:
      </label> 
        <input
          type="text"
          className='form-control'
          value={courseData.minSalary}
          onChange={(e) =>
            setCourseData({ ...courseData, minSalary: e.target.value })
          }
        />
     
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      HighestSalary:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.HighestSalary}
          onChange={(e) =>
            setCourseData({ ...courseData, HighestSalary: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      BatchStarting:
      </label> 
        <input
          type="text"
          className='form-control'
          value={courseData.BatchStarting}
          onChange={(e) =>
            setCourseData({ ...courseData, BatchStarting: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      heroTitle:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.heroTitle}
          onChange={(e) =>
            setCourseData({ ...courseData, heroTitle: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      heroSubtitle:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.heroSubtitle}
          onChange={(e) =>
            setCourseData({ ...courseData, heroSubtitle: e.target.value })
          }
        />
      
        </div>
        <div className="col-12 col-md-6 col-lg-3">
        <label className='form-label'>
      courseImage:
      </label>
        <input
          type="text"
          className='form-control'
          value={courseData.courseImage}
          onChange={(e) =>
            setCourseData({ ...courseData, courseImage: e.target.value })
          }
        />
     
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">
        <label className='form-label'>
      courseDescription:
      </label>
        {/* <input
          type="text"
          value={courseData.courseDescription}
          onChange={(e) =>
            setCourseData({ ...courseData, courseDescription: e.target.value })
          }
        /> */}
         <ReactQuill  value={courseData.courseDescription} onChange={handleCourseDescriptionChange} className='form-control' />
      
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">
        <label className='form-label'>
      certification:
      </label>
        {/* <input
          type="text"
          value={courseData.certification}
          onChange={(e) =>
            setCourseData({ ...courseData, certification: e.target.value })
          }
        /> */}
          <ReactQuill  value={courseData.certification} onChange={handleCertificationChange} className='form-control' />
      
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">
        <label className='form-label'>
      courseFor:
      </label>
        {/* <input
          type="text"
          value={courseData.courseFor}
          onChange={(e) =>
            setCourseData({ ...courseData, courseFor: e.target.value })
          }
        /> */}
        <ReactQuill  value={courseData.courseFor} onChange={handleCourseForChange} className='form-control' />
      
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">
        <label className='form-label'>
      designation:
      </label>
        {/* <input
          type="text"
          value={courseData.designation}
          onChange={(e) =>
            setCourseData({ ...courseData, designation: e.target.value })
          }
        /> */}
        <ReactQuill  value={courseData.designation} onChange={handleDesignationChange} className='form-control' />
      
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">
        <label className='form-label'>
      salaryDescription:
      </label>
        {/* <input
          type="text"
          value={courseData.salaryDescription}
          onChange={(e) =>
            setCourseData({ ...courseData, salaryDescription: e.target.value })
          }
        /> */}
        <ReactQuill  value={courseData.salaryDescription} onChange={handleSalaryDescriptionChange} className='form-control' />
     
        </div>
        <div className="col-12 col-sm-12 col-md-6 col-lg-6">

        <label className='form-label'>
      courseHeroPoints:
      </label>
        {/* <input
          type="text"
          value={courseData.courseHeroPoints}
          onChange={(e) =>
            setCourseData({ ...courseData, courseHeroPoints: e.target.value })
          }
        /> */}
        <ReactQuill  value={courseData.courseHeroPoints} onChange={handleCourseHeroPointsChange} className='form-control' />
      
        </div>


      </div>
      
 
     
    
      
     
     
      
      
      {/* Add other course details input fields as needed */}

     <div className="faq bg-white p-3">
     <h2>FAQs</h2>
      {courseData.faqs.map((faq, index) => (
  <div key={index}>
    <label htmlFor="faq" className='form-label'>frequently asked question</label>
    <input
      type="text"
      placeholder="Question"
      className='form-control'
      value={faq.question}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          faqs: prevData.faqs.map((item, i) =>
            i === index ? { ...item, question: e.target.value } : item
          ),
        }))
      }
    />
    <textarea
      type="text"
      placeholder="Answer"
      value={faq.answer}
      className='form-control'
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          faqs: prevData.faqs.map((item, i) =>
            i === index ? { ...item, answer: e.target.value } : item
          ),
        }))
      }
    />
  </div>
))}

      <button  className='main-btn' onClick={handleAddFAQ}>Add FAQ</button>

     </div>
    <div className="module bg-white p-3">
    <h2>Modules</h2>
      {courseData.modules.map((module, index) => (
  <div key={index}>
    <input
      type="text"
      placeholder="Module Title"
      value={module.title}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          modules: prevData.modules.map((item, i) =>
            i === index ? { ...item, title: e.target.value } : item
          ),
        }))
      }
    />
    <ReactQuill
      value={module.topics}
      onChange={(value) =>
        setCourseData((prevData) => ({
          ...prevData,
          modules: prevData.modules.map((item, i) =>
            i === index ? { ...item, topics: value } : item
          ),
        }))
      }
    />
  </div>
))}

      <button  onClick={handleAddModule} className='main-btn'>Add Module</button>
    </div>

      <h2>Programming Languages</h2>
      {courseData.programmingLanguages.map((language, index) => (
  <div key={index}>
    <input
      type="text"
      placeholder="Language Name"
      value={language.name}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          programmingLanguages: prevData.programmingLanguages.map(
            (item, i) =>
              i === index ? { ...item, name: e.target.value } : item
          ),
        }))
      }
    />
    <input
      type="text"
      placeholder="Image URL"
      value={language.image}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          programmingLanguages: prevData.programmingLanguages.map(
            (item, i) =>
              i === index ? { ...item, image: e.target.value } : item
          ),
        }))
      }
    />
  </div>
))}
<button className='main-btn' onClick={handleAddProgrammingLanguage}>
  Add Programming Language
</button>

      <button  className='main-btn' onClick={handleAddProgrammingLanguage}>
        Add Programming Language
      </button>
      {courseData.instructors.map((instructor, index) => (
  <div key={index}>
    <input
      type="text"
      placeholder="Instructor Name"
      value={instructor.name}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          instructors: prevData.instructors.map((item, i) =>
            i === index ? { ...item, name: e.target.value } : item
          ),
        }))
      }
    />
    <input
      type="text"
      placeholder="Instructor Email"
      value={instructor.email}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          instructors: prevData.instructors.map((item, i) =>
            i === index ? { ...item, email: e.target.value } : item
          ),
        }))
      }
    />
     <input
      type="text"
      placeholder="Enrolled"
      value={instructor.enrolled}
      onChange={(e) =>
        setCourseData((prevData) => ({
          ...prevData,
          instructors: prevData.instructors.map((item, i) =>
            i === index ? { ...item, enrolled: e.target.value } : item
          ),
        }))
      }
    />
  </div>
))}
<button onClick={handleAddInstructor} className='main-btn'>Add Instructor</button>



      <button onClick={handleUpload} className='main-btn'>Upload Course</button>
      
    </div>
  );
};

export default UploadData;
