import React, { useState } from 'react';
import axios from 'axios';

function EventsUpload({ onUpload }) {
  const [formData, setFormData] = useState({
    image: '',
    title: '',
    tag: '',
    content: '',
    date: '',
    time: '',
    BookTicketLink: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    // Handle file upload, you can use a library for this (e.g., FormData).
  };

  const handleEventUpload = () => {
    // Send a POST request to create a new event using formData.
    axios.post('https://api/events', formData)
      .then((response) => {
        // Handle success and add the new event to the list.
        onUpload(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Event Upload</h2>
      <form>
        <div>
          <label className='form-label'>Title:</label>
          <input type="text" name="title" onChange={handleInputChange} value={formData.title} className='form-control mb-3' />
        </div>
        <div>
          <label className='form-label'>Tag:</label>
          <input type="text" name="tag" onChange={handleInputChange} value={formData.tag} className='form-control mb-3' />
        </div>
        <div>
          <label className='form-label'>Content:</label>
          <textarea name="content" onChange={handleInputChange} value={formData.content} className='form-control mb-3'/>
        </div>
        <div>
          <label className='form-label'>Date:</label>
          <input type="date" name="date" onChange={handleInputChange} value={formData.date}  className='form-control mb-3'/>
        </div>
        <div>
          <label className='form-label'>Time:</label>
          <input type="time" name="time" onChange={handleInputChange} value={formData.time} className='form-control mb-3'/>
        </div>
        <div>
          <label className='form-label'>Book Ticket Link:</label>
          <input type="text" name="BookTicketLink" onChange={handleInputChange} value={formData.BookTicketLink} className='form-control mb-3'/>
        </div>
        <div>
          <label className='form-label'>Event Image:</label>
          <input type="file" name="image" onChange={handleFileChange} accept="image/*" />
        </div>
        <button type="button" onClick={handleEventUpload}>Upload Event</button>
      </form>
    </div>
  );
}

export default EventsUpload;
